<script>
import { defineComponent, ref, shallowRef } from "vue";
import { AgGridVue } from "ag-grid-vue3";
import "ag-grid-community/styles/ag-grid.css";
import "ag-grid-community/styles/ag-theme-quartz.css";

export default defineComponent({
  components: {
    "ag-grid-vue": AgGridVue,
  },
  setup() {
    const columnDefs = ref([
      { field: "DEV_NO", minWidth: 170, checkboxSelection: true, headerCheckboxSelection: true },
      { field: "NM" },
      { field: "PJ_INP_STTS" },
      { field: "CTRT_NMTM" },
      { field: "BRDT" },
      { field: "GNDR" },
      { field: "JBPS" },
      { field: "GRD" },
      { field: "T_CR_PER" },
      { field: "RGN" },
      { field: "MBL_TELNO" },
      { field: "EML" },
      { field: "CONTT_MTHD" },
      { field: "NTRV_DMND_DT" },
      { field: "INPUT_PSBLTY_DT" },
      { field: "OGDP_CO" },
      { field: "SN" },
      { field: "WHTAX_YN" },
      { field: "BZMN_YN" },
      { field: "KDS_EMP_YN" },
      { field: "CTRT_CO_EMP_YN" },
      { field: "CLCT_PICKUP_DT" },
      { field: "GIVE_DT" },
      { field: "BANK" },
      { field: "ACTNO" },
      { field: "DEPT" },
      { field: "MM_DMND_UNTPRC" },
      { field: "ADDR" },
      { field: "JBTTL" },
      { field: "BRKR" },
      { field: "KAKAO_NICK" },
      { field: "CTRT_HSTRY_YN" },
      { field: "MS" },
    ]);

    const gridApi = shallowRef();
    const defaultColDef = ref({
      editable: true,
      filter: true,
      flex: 1,
      minWidth: 100,
    });
    const rowSelection = ref("multiple");
    const rowData = ref([]);

    const onGridReady = (params) => {
      gridApi.value = params.api;
      fetch("http://localhost:8080/api/data") // API 주소로 변경
          .then((resp) => resp.json())
          .then((data) => {
            rowData.value = data; // 백엔드에서 가져온 데이터
          })
          .catch((error) => {
            console.error("데이터 로드 오류:", error);
          });
    };

    return {
      columnDefs,
      gridApi,
      defaultColDef,
      rowSelection,
      rowData,
      onGridReady,
    };
  },
});
</script>
