let handlers = {
    deleteRow: [],
    search: [],
    reset: [],
    filterUpdate: []
};

export default {
    SearchResultEvent: {
        add(eventType, handler) {
            if (handlers[eventType]) {
                handlers[eventType].push(handler);
            }
        },
        deleteRowBtnClick() {
            handlers.deleteRow.forEach(handler => handler()); // deleteRow 핸들러만 호출
        },
        fetchData() {
            handlers.search.forEach(handler => handler()); // search 핸들러만 호출
        },
        resetFilter(){
            handlers.reset.forEach(handler => handler());// reset 핸들러만 호출
        },
        toggleFilterButton() {
            handlers.toggleFilterButton.forEach(handler => handler()); // 버튼 상태 핸들러 호출
        },
    }
}
