let handlers = {
    deleteRow: [],
    search: [],
    reset: [],
    filterUpdate: [],
    resetButtons: []
};

export { handlers };

export default {
    SearchResultEvent: {
        add(eventType, handler) {
            if (handlers[eventType]) {
                handlers[eventType].push(handler);
            }
        },
        deleteRowBtnClick() {
            handlers.deleteRow.forEach(handler => handler());
        },
        fetchData(type, filter) {
            handlers.search.forEach(handler => handler(type, filter));
        },
        resetFilter() {
            handlers.reset.forEach(handler => handler());
        },
        filterUpdate(type, filter) {
            handlers.filterUpdate = handlers.filterUpdate.filter(handler => {
                // 동일한 type과 filter를 가진 핸들러를 삭제
                return !(handler.type === type && handler.filter === filter);
            });
        },
        resetButtons() {
            handlers.resetButtons.forEach(handler => handler());
        },
    }
}
